  object-fit: cover;
}

.preview__description {
  display: grid;
  width: 100%;
  grid-template-columns: 1fr 2fr 2rem;
  row-gap: .5rem;
  align-items: center;
  margin: 2rem 1rem;
  /* background-color: #ccc; */
}

.preview__title {
  grid-column: 1/-1;
  font-size: 2rem;    
  text-transform: uppercase;
  font-weight: 600;
  /* This is how text is truncated! */
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.preview__rating {
  grid-column: 1/span 1;
  font-size: 1.4rem;
  font-weight: 200;
}
.preview__genre {
  grid-column: 2/span 1;
  font-size: 1.2rem;
  font-weight: 200;
  font-style: italic;
}
.preview__overiew {
  grid-column: 1/-1;
  font-size: 1.3rem;    
}

